  1028→    window.addEventListener('scroll', () => {
  1029→      nav.classList.toggle('scrolled', window.scrollY > 20);
  1030→    }, { passive: true });
  1031→
  1032→    // Mobile menu
  1033→    const navToggle = document.getElementById('navToggle');
  1034→    const navLinks = document.getElementById('navLinks');
  1035→    navToggle.addEventListener('click', () => {
  1036→      const isOpen = navLinks.classList.toggle('open');
  1037→      navToggle.classList.toggle('active');
  1038→      navToggle.setAttribute('aria-expanded', isOpen);
  1039→      navToggle.setAttribute('aria-label', isOpen ? 'Menü schließen' : 'Menü öffnen');
  1040→      document.body.style.overflow = isOpen ? 'hidden' : '';
  1041→    });
  1042→    navLinks.querySelectorAll('a').forEach(link => {
  1043→      link.addEventListener('click', () => {
  1044→        navLinks.classList.remove('open');
  1045→        navToggle.classList.remove('active');
  1046→        navToggle.setAttribute('aria-expanded', 'false');
  1047→        document.body.style.overflow = '';
  1048→      });
  1049→    });
  1050→
  1051→    // Scroll reveal
  1052→    const observer = new IntersectionObserver((entries) => {
  1053→      entries.forEach(e => {
  1054→        if (e.isIntersecting) { e.target.classList.add('visible'); observer.unobserve(e.target); }
  1055→      });
  1056→    }, { threshold: 0.1, rootMargin: '0px 0px -30px 0px' });
  1057→    document.querySelectorAll('.reveal').forEach(el => observer.observe(el));
  1058→
  1059→    // Stagger children
  1060→    document.querySelectorAll('.features, .board-grid').forEach(grid => {
  1061→      grid.querySelectorAll('.reveal').forEach((c, i) => { c.style.transitionDelay = `${i * .08}s`; });
  1062→    });
