          <i data-lucide="bell-ring" style="width:28px;height:28px;color:var(--polizei-mid);flex-shrink:0;"></i>
          <span class="partner-item__info">
            <span class="partner-item__name">safeREACH.com</span>
            <span class="partner-item__desc">Alarmierungslösung</span>
          </span>
        </a>
      </div>
    </div>
  </section>
