     1‚Üí<!DOCTYPE html>
     2‚Üí<html lang="de">
     3‚Üí<head>
     4‚Üí  <meta charset="UTF-8">
     5‚Üí  <meta name="viewport" content="width=device-width, initial-scale=1.0">
     6‚Üí  <title>BOS-ARSA ‚Äî Amateurfunk im Dienst der Sicherheit</title>
     7‚Üí  <meta name="description" content="√ñsterreichischer Amateurfunkverein der Beh√∂rden und Organisationen mit Sicherheitsaufgaben ‚Äì BOS-Amateur-Radio-Society-Austria">
     8‚Üí  <meta property="og:title" content="BOS-ARSA ‚Äî Amateurfunk im Dienst der Sicherheit">
     9‚Üí  <meta property="og:description" content="Der √∂sterreichische Amateurfunkverein der Beh√∂rden und Organisationen mit Sicherheitsaufgaben.">
    10‚Üí  <meta property="og:type" content="website">
    11‚Üí  <meta property="og:url" content="https://bos-arsa.at/">
    12‚Üí  <meta property="og:locale" content="de_AT">
    13‚Üí  <link rel="preconnect" href="https://fonts.googleapis.com">
    14‚Üí  <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    15‚Üí  <link href="https://fonts.googleapis.com/css2?family=Barlow+Condensed:wght@400;600;700;800&family=Source+Sans+3:wght@300;400;500;600;700&family=JetBrains+Mono:wght@400;500&display=swap" rel="stylesheet">
    16‚Üí  <script src="https://unpkg.com/lucide@latest"></script>
    17‚Üí  <style>
    18‚Üí    /* ===== CSS Reset & Custom Properties ===== */
    19‚Üí    *, *::before, *::after {
    20‚Üí      margin: 0;
    21‚Üí      padding: 0;
    22‚Üí      box-sizing: border-box;
    23‚Üí    }
    24‚Üí
    25‚Üí    :root {
    26‚Üí      --bos-navy:        #0A1628;
    27‚Üí      --bos-blue:        #003DA5;
    28‚Üí      --bos-blue-light:  #1A5FC7;
    29‚Üí      --bos-blue-bright: #2E7DE6;
    30‚Üí      --bos-silver:      #C0C8D4;
    31‚Üí      --bos-gold:        #C9A84C;
    32‚Üí      --bos-white:       #F0F2F5;
    33‚Üí      --bos-red:         #CC2936;
    34‚Üí      --bos-dark:        #0D1B2A;
    35‚Üí      --bos-gray:        #374151;
    36‚Üí
    37‚Üí      --font-display: 'Barlow Condensed', 'Arial Narrow', sans-serif;
    38‚Üí      --font-body: 'Source Sans 3', 'Source Sans Pro', sans-serif;
    39‚Üí      --font-mono: 'JetBrains Mono', monospace;
    40‚Üí
    41‚Üí      --container: 1200px;
    42‚Üí      --nav-height: 64px;
    43‚Üí    }
    44‚Üí
    45‚Üí    html {
    46‚Üí      scroll-behavior: smooth;
    47‚Üí      scroll-padding-top: var(--nav-height);
    48‚Üí    }
    49‚Üí
    50‚Üí    body {
    51‚Üí      font-family: var(--font-body);
    52‚Üí      color: var(--bos-dark);
    53‚Üí      background: var(--bos-white);
    54‚Üí      line-height: 1.6;
    55‚Üí      -webkit-font-smoothing: antialiased;
    56‚Üí      -moz-osx-font-smoothing: grayscale;
    57‚Üí    }
    58‚Üí
    59‚Üí    img {
    60‚Üí      max-width: 100%;
    61‚Üí      display: block;
    62‚Üí    }
    63‚Üí
    64‚Üí    a {
    65‚Üí      color: var(--bos-blue-light);
    66‚Üí      text-decoration: none;
    67‚Üí      transition: color 0.2s;
    68‚Üí    }
    69‚Üí
    70‚Üí    a:hover {
    71‚Üí      color: var(--bos-blue-bright);
    72‚Üí    }
    73‚Üí
    74‚Üí    a:focus-visible {
    75‚Üí      outline: 2px solid var(--bos-blue-bright);
    76‚Üí      outline-offset: 2px;
    77‚Üí    }
    78‚Üí
    79‚Üí    .container {
    80‚Üí      max-width: var(--container);
    81‚Üí      margin: 0 auto;
    82‚Üí      padding: 0 24px;
    83‚Üí    }
    84‚Üí
    85‚Üí    /* ===== Section Divider ===== */
    86‚Üí    .section-divider {
    87‚Üí      width: 60px;
    88‚Üí      height: 2px;
    89‚Üí      background: var(--bos-gold);
    90‚Üí      margin: 16px auto 0;
    91‚Üí    }
    92‚Üí
    93‚Üí    .section-divider--left {
    94‚Üí      margin-left: 0;
    95‚Üí    }
    96‚Üí
    97‚Üí    /* ===== Scroll Animations ===== */
    98‚Üí    .reveal {
    99‚Üí      opacity: 0;
   100‚Üí      transform: translateY(30px);
   101‚Üí      transition: opacity 0.7s cubic-bezier(0.22, 1, 0.36, 1), transform 0.7s cubic-bezier(0.22, 1, 0.36, 1);
   102‚Üí    }
   103‚Üí
   104‚Üí    .reveal.visible {
   105‚Üí      opacity: 1;
   106‚Üí      transform: translateY(0);
   107‚Üí    }
   108‚Üí
   109‚Üí    /* ===== Navigation ===== */
   110‚Üí    .nav {
   111‚Üí      position: fixed;
   112‚Üí      top: 0;
   113‚Üí      left: 0;
   114‚Üí      right: 0;
   115‚Üí      z-index: 1000;
   116‚Üí      background: var(--bos-navy);
   117‚Üí      border-bottom: 1px solid rgba(192, 200, 212, 0.1);
   118‚Üí      height: var(--nav-height);
   119‚Üí      transition: box-shadow 0.3s;
   120‚Üí    }
   121‚Üí
   122‚Üí    .nav.scrolled {
   123‚Üí      box-shadow: 0 2px 20px rgba(0, 0, 0, 0.4);
   124‚Üí    }
   125‚Üí
   126‚Üí    .nav__inner {
   127‚Üí      display: flex;
   128‚Üí      align-items: center;
   129‚Üí      justify-content: space-between;
   130‚Üí      height: 100%;
   131‚Üí    }
   132‚Üí
   133‚Üí    .nav__brand {
   134‚Üí      display: flex;
   135‚Üí      align-items: center;
   136‚Üí      gap: 10px;
   137‚Üí      color: #fff;
   138‚Üí      font-family: var(--font-display);
   139‚Üí      font-weight: 800;
   140‚Üí      font-size: 1.4rem;
   141‚Üí      letter-spacing: 1px;
   142‚Üí      text-transform: uppercase;
   143‚Üí      text-decoration: none;
   144‚Üí    }
   145‚Üí
   146‚Üí    .nav__brand:hover {
   147‚Üí      color: #fff;
   148‚Üí    }
   149‚Üí
   150‚Üí    .nav__brand-icon {
   151‚Üí      position: relative;
   152‚Üí      width: 38px;
   153‚Üí      height: 38px;
   154‚Üí      display: flex;
   155‚Üí      align-items: center;
   156‚Üí      justify-content: center;
   157‚Üí      background: linear-gradient(135deg, var(--bos-blue), var(--bos-blue-light));
   158‚Üí      border: 1px solid var(--bos-gold);
   159‚Üí      border-radius: 3px;
   160‚Üí    }
   161‚Üí
   162‚Üí    .nav__brand-icon i {
   163‚Üí      color: var(--bos-gold);
   164‚Üí    }
   165‚Üí
   166‚Üí    .nav__links {
   167‚Üí      display: flex;
   168‚Üí      align-items: center;
   169‚Üí      gap: 8px;
   170‚Üí      list-style: none;
   171‚Üí    }
   172‚Üí
   173‚Üí    .nav__links a {
   174‚Üí      display: block;
   175‚Üí      padding: 8px 14px;
   176‚Üí      color: var(--bos-silver);
   177‚Üí      font-family: var(--font-body);
   178‚Üí      font-size: 0.9rem;
   179‚Üí      font-weight: 500;
   180‚Üí      letter-spacing: 0.3px;
   181‚Üí      transition: color 0.2s, background 0.2s;
   182‚Üí      border-radius: 3px;
   183‚Üí      text-decoration: none;
   184‚Üí    }
   185‚Üí
   186‚Üí    .nav__links a:hover,
   187‚Üí    .nav__links a:focus-visible {
   188‚Üí      color: #fff;
   189‚Üí      background: rgba(255, 255, 255, 0.06);
   190‚Üí    }
   191‚Üí
   192‚Üí    /* Hamburger */
   193‚Üí    .nav__hamburger {
   194‚Üí      display: none;
   195‚Üí      flex-direction: column;
   196‚Üí      justify-content: center;
   197‚Üí      gap: 5px;
   198‚Üí      width: 36px;
   199‚Üí      height: 36px;
   200‚Üí      background: none;
   201‚Üí      border: none;
   202‚Üí      cursor: pointer;
   203‚Üí      padding: 4px;
   204‚Üí    }
   205‚Üí
   206‚Üí    .nav__hamburger span {
   207‚Üí      display: block;
   208‚Üí      width: 100%;
   209‚Üí      height: 2px;
   210‚Üí      background: var(--bos-silver);
   211‚Üí      transition: transform 0.3s, opacity 0.3s;
   212‚Üí    }
   213‚Üí
   214‚Üí    .nav__hamburger.active span:nth-child(1) {
   215‚Üí      transform: translateY(7px) rotate(45deg);
   216‚Üí    }
   217‚Üí
   218‚Üí    .nav__hamburger.active span:nth-child(2) {
   219‚Üí      opacity: 0;
   220‚Üí    }
   221‚Üí
   222‚Üí    .nav__hamburger.active span:nth-child(3) {
   223‚Üí      transform: translateY(-7px) rotate(-45deg);
   224‚Üí    }
   225‚Üí
   226‚Üí    /* ===== Hero Section ===== */
   227‚Üí    .hero {
   228‚Üí      position: relative;
   229‚Üí      min-height: 100vh;
   230‚Üí      display: flex;
   231‚Üí      align-items: center;
   232‚Üí      justify-content: center;
   233‚Üí      background: linear-gradient(160deg, var(--bos-navy) 0%, #0e2240 40%, var(--bos-blue) 100%);
   234‚Üí      overflow: hidden;
   235‚Üí      padding: calc(var(--nav-height) + 40px) 24px 80px;
   236‚Üí    }
   237‚Üí
   238‚Üí    /* Geometric grid pattern overlay */
   239‚Üí    .hero__pattern {
   240‚Üí      position: absolute;
   241‚Üí      inset: 0;
   242‚Üí      opacity: 0.04;
   243‚Üí      background-image:
   244‚Üí        linear-gradient(rgba(255,255,255,0.1) 1px, transparent 1px),
   245‚Üí        linear-gradient(90deg, rgba(255,255,255,0.1) 1px, transparent 1px);
   246‚Üí      background-size: 60px 60px;
   247‚Üí    }
   248‚Üí
   249‚Üí    /* Radio wave animation */
   250‚Üí    .hero__waves {
   251‚Üí      position: absolute;
   252‚Üí      top: 50%;
   253‚Üí      left: 50%;
   254‚Üí      transform: translate(-50%, -50%);
   255‚Üí      pointer-events: none;
   256‚Üí    }
   257‚Üí
   258‚Üí    .hero__wave {
   259‚Üí      position: absolute;
   260‚Üí      top: 50%;
   261‚Üí      left: 50%;
   262‚Üí      width: 200px;
   263‚Üí      height: 200px;
   264‚Üí      margin-top: -100px;
   265‚Üí      margin-left: -100px;
   266‚Üí      border: 1px solid rgba(201, 168, 76, 0.15);
   267‚Üí      border-radius: 50%;
   268‚Üí      animation: wave-expand 4s ease-out infinite;
   269‚Üí    }
   270‚Üí
   271‚Üí    .hero__wave:nth-child(2) { animation-delay: 1s; }
   272‚Üí    .hero__wave:nth-child(3) { animation-delay: 2s; }
   273‚Üí    .hero__wave:nth-child(4) { animation-delay: 3s; }
   274‚Üí
   275‚Üí    @keyframes wave-expand {
   276‚Üí      0% {
   277‚Üí        width: 200px;
   278‚Üí        height: 200px;
   279‚Üí        margin-top: -100px;
   280‚Üí        margin-left: -100px;
   281‚Üí        opacity: 0.5;
   282‚Üí      }
   283‚Üí      100% {
   284‚Üí        width: 1200px;
   285‚Üí        height: 1200px;
   286‚Üí        margin-top: -600px;
   287‚Üí        margin-left: -600px;
   288‚Üí        opacity: 0;
   289‚Üí      }
   290‚Üí    }
   291‚Üí
   292‚Üí    .hero__content {
   293‚Üí      position: relative;
   294‚Üí      z-index: 2;
   295‚Üí      text-align: center;
   296‚Üí      max-width: 820px;
   297‚Üí    }
   298‚Üí
   299‚Üí    .hero__badge {
   300‚Üí      display: inline-flex;
   301‚Üí      align-items: center;
   302‚Üí      gap: 8px;
   303‚Üí      padding: 6px 16px;
   304‚Üí      background: rgba(201, 168, 76, 0.12);
   305‚Üí      border: 1px solid rgba(201, 168, 76, 0.25);
   306‚Üí      border-radius: 3px;
   307‚Üí      color: var(--bos-gold);
   308‚Üí      font-family: var(--font-mono);
   309‚Üí      font-size: 0.78rem;
   310‚Üí      letter-spacing: 2px;
   311‚Üí      text-transform: uppercase;
   312‚Üí      margin-bottom: 28px;
   313‚Üí    }
   314‚Üí
   315‚Üí    .hero__title {
   316‚Üí      font-family: var(--font-display);
   317‚Üí      font-size: clamp(2.4rem, 6vw, 4.2rem);
   318‚Üí      font-weight: 800;
   319‚Üí      color: #fff;
   320‚Üí      line-height: 1.1;
   321‚Üí      letter-spacing: -0.5px;
   322‚Üí      text-transform: uppercase;
   323‚Üí      margin-bottom: 20px;
   324‚Üí    }
   325‚Üí
   326‚Üí    .hero__title span {
   327‚Üí      color: var(--bos-gold);
   328‚Üí    }
   329‚Üí
   330‚Üí    .hero__subtitle {
   331‚Üí      font-size: clamp(1rem, 2vw, 1.2rem);
   332‚Üí      color: var(--bos-silver);
   333‚Üí      font-weight: 300;
   334‚Üí      line-height: 1.6;
   335‚Üí      max-width: 640px;
   336‚Üí      margin: 0 auto 40px;
   337‚Üí    }
   338‚Üí
   339‚Üí    .hero__actions {
   340‚Üí      display: flex;
   341‚Üí      gap: 16px;
   342‚Üí      justify-content: center;
   343‚Üí      flex-wrap: wrap;
   344‚Üí    }
   345‚Üí
   346‚Üí    /* ===== Buttons ===== */
   347‚Üí    .btn {
   348‚Üí      display: inline-flex;
   349‚Üí      align-items: center;
   350‚Üí      gap: 8px;
   351‚Üí      padding: 14px 32px;
   352‚Üí      font-family: var(--font-display);
   353‚Üí      font-size: 1rem;
   354‚Üí      font-weight: 700;
   355‚Üí      letter-spacing: 1px;
   356‚Üí      text-transform: uppercase;
   357‚Üí      border: 2px solid transparent;
   358‚Üí      border-radius: 3px;
   359‚Üí      cursor: pointer;
   360‚Üí      transition: all 0.25s;
   361‚Üí      text-decoration: none;
   362‚Üí    }
   363‚Üí
   364‚Üí    .btn:focus-visible {
   365‚Üí      outline: 2px solid var(--bos-blue-bright);
   366‚Üí      outline-offset: 2px;
   367‚Üí    }
   368‚Üí
   369‚Üí    .btn--primary {
   370‚Üí      background: linear-gradient(135deg, var(--bos-gold), #d4b35a);
   371‚Üí      color: var(--bos-navy);
   372‚Üí      border-color: var(--bos-gold);
   373‚Üí    }
   374‚Üí
   375‚Üí    .btn--primary:hover {
   376‚Üí      background: linear-gradient(135deg, #d4b35a, #dfc06e);
   377‚Üí      color: var(--bos-navy);
   378‚Üí      transform: translateY(-2px);
   379‚Üí      box-shadow: 0 4px 20px rgba(201, 168, 76, 0.3);
   380‚Üí    }
   381‚Üí
   382‚Üí    .btn--outline {
   383‚Üí      background: transparent;
   384‚Üí      color: #fff;
   385‚Üí      border-color: rgba(255, 255, 255, 0.3);
   386‚Üí    }
   387‚Üí
   388‚Üí    .btn--outline:hover {
   389‚Üí      border-color: #fff;
   390‚Üí      color: #fff;
   391‚Üí      background: rgba(255, 255, 255, 0.06);
   392‚Üí      transform: translateY(-2px);
   393‚Üí    }
   394‚Üí
   395‚Üí    .btn--blue {
   396‚Üí      background: var(--bos-blue-light);
   397‚Üí      color: #fff;
   398‚Üí      border-color: var(--bos-blue-light);
   399‚Üí    }
   400‚Üí
   401‚Üí    .btn--blue:hover {
   402‚Üí      background: var(--bos-blue-bright);
   403‚Üí      border-color: var(--bos-blue-bright);
   404‚Üí      color: #fff;
   405‚Üí      transform: translateY(-2px);
   406‚Üí      box-shadow: 0 4px 20px rgba(46, 125, 230, 0.3);
   407‚Üí    }
   408‚Üí
   409‚Üí    /* ===== Section Shared Styles ===== */
   410‚Üí    .section {
   411‚Üí      padding: 100px 0;
   412‚Üí    }
   413‚Üí
   414‚Üí    .section--dark {
   415‚Üí      background: linear-gradient(160deg, var(--bos-dark), var(--bos-navy));
   416‚Üí      color: #fff;
   417‚Üí    }
   418‚Üí
   419‚Üí    .section__header {
   420‚Üí      text-align: center;
   421‚Üí      margin-bottom: 64px;
   422‚Üí    }
   423‚Üí
   424‚Üí    .section__label {
   425‚Üí      display: inline-block;
   426‚Üí      font-family: var(--font-mono);
   427‚Üí      font-size: 0.78rem;
   428‚Üí      letter-spacing: 3px;
   429‚Üí      text-transform: uppercase;
   430‚Üí      color: var(--bos-gold);
   431‚Üí      margin-bottom: 12px;
   432‚Üí    }
   433‚Üí
   434‚Üí    .section__title {
   435‚Üí      font-family: var(--font-display);
   436‚Üí      font-size: clamp(1.8rem, 4vw, 2.6rem);
   437‚Üí      font-weight: 800;
   438‚Üí      text-transform: uppercase;
   439‚Üí      letter-spacing: 0.5px;
   440‚Üí      line-height: 1.15;
   441‚Üí    }
   442‚Üí
   443‚Üí    .section--dark .section__title {
   444‚Üí      color: #fff;
   445‚Üí    }
   446‚Üí
   447‚Üí    .section__intro {
   448‚Üí      max-width: 700px;
   449‚Üí      margin: 24px auto 0;
   450‚Üí      color: var(--bos-gray);
   451‚Üí      font-size: 1.08rem;
   452‚Üí      line-height: 1.7;
   453‚Üí      text-align: center;
   454‚Üí    }
   455‚Üí
   456‚Üí    .section--dark .section__intro {
   457‚Üí      color: var(--bos-silver);
   458‚Üí    }
   459‚Üí
   460‚Üí    /* ===== About / Feature Cards ===== */
   461‚Üí    .features {
   462‚Üí      display: grid;
   463‚Üí      grid-template-columns: repeat(3, 1fr);
   464‚Üí      gap: 28px;
   465‚Üí    }
   466‚Üí
   467‚Üí    .feature-card {
   468‚Üí      background: #fff;
   469‚Üí      border: 1px solid rgba(192, 200, 212, 0.4);
   470‚Üí      border-radius: 4px;
   471‚Üí      padding: 40px 32px;
   472‚Üí      transition: transform 0.3s, box-shadow 0.3s, border-color 0.3s;
   473‚Üí      position: relative;
   474‚Üí      overflow: hidden;
   475‚Üí    }
   476‚Üí
   477‚Üí    .feature-card::before {
   478‚Üí      content: '';
   479‚Üí      position: absolute;
   480‚Üí      top: 0;
   481‚Üí      left: 0;
   482‚Üí      right: 0;
   483‚Üí      height: 3px;
   484‚Üí      background: linear-gradient(90deg, var(--bos-blue), var(--bos-blue-bright));
   485‚Üí      transform: scaleX(0);
   486‚Üí      transform-origin: left;
   487‚Üí      transition: transform 0.4s;
   488‚Üí    }
   489‚Üí
