  1362‚Üí      link.addEventListener('click', () => {
  1363‚Üí        navLinks.classList.remove('open');
  1364‚Üí        navToggle.classList.remove('active');
  1365‚Üí        navToggle.setAttribute('aria-expanded', 'false');
  1366‚Üí        navToggle.setAttribute('aria-label', 'Men√º √∂ffnen');
  1367‚Üí        document.body.style.overflow = '';
  1368‚Üí      });
  1369‚Üí    });
  1370‚Üí
  1371‚Üí    // Scroll reveal animation with IntersectionObserver
  1372‚Üí    const revealElements = document.querySelectorAll('.reveal');
  1373‚Üí    const revealObserver = new IntersectionObserver((entries) => {
  1374‚Üí      entries.forEach(entry => {
  1375‚Üí        if (entry.isIntersecting) {
  1376‚Üí          entry.target.classList.add('visible');
  1377‚Üí          revealObserver.unobserve(entry.target);
  1378‚Üí        }
  1379‚Üí      });
  1380‚Üí    }, {
  1381‚Üí      threshold: 0.12,
  1382‚Üí      rootMargin: '0px 0px -40px 0px'
  1383‚Üí    });
  1384‚Üí
  1385‚Üí    revealElements.forEach(el => revealObserver.observe(el));
  1386‚Üí
  1387‚Üí    // Stagger animation for cards in the same row
  1388‚Üí    document.querySelectorAll('.features, .board-grid').forEach(grid => {
  1389‚Üí      const cards = grid.querySelectorAll('.reveal');
  1390‚Üí      cards.forEach((card, i) => {
  1391‚Üí        card.style.transitionDelay = `${i * 0.1}s`;
  1392‚Üí      });
  1393‚Üí    });
